import Drink from "./Drinks";

class Drinks extends React.Component{

  state = {
    datas: this.props.datas,
    loading: true
  }

  componentDidMount(){
    setTimeout(()=>this.setState({loading: false}),6000)

  }

  render(){
    if (this.state.loading){
      return <p>Loading</p>
    } else{
      return(
        <div className="Drinks">
          <div className="row">
          {this.state.datas.drinks.map(item => {
            return <Drink key={item.idDrink} strDrink={item.strDrink} strDrinkThumb={item.strDrinkThumb} />
          })}
          </div>
        </div>
      )
    }
  }

}

export default Drinks
