import Fetch from "isomorphic-unfetch";

export default class SearchBar extends React.Component{

  constructor(props){
    super(props)
    this.state={
      options: this.props.options,
      searchValue:"",
      currentFilter: "Ordinary Drink"
    }
  }

  render(){
    return(
      <form>
        <div className="row">
        {/*}
        <div className="col">
          <div className="form-group">
            <label htmlFor="sort">Drink Name</label>
            <input className="form-control" value={this.props.searchValue} onChange={(event) => this.props.searchDrink(event.target.value)} placeholder="Enter your drinks name..."/>
          </div>
        </div>
        */}

          <div className="col">
              <div className="form-group">
                <label htmlFor="cocktailFilter">Drink Type </label>
                <select className="form-control" id="cocktailFilter" onChange={(event)=> this.selectedChange()}>
                  {this.state.options.drinks.map(item => {
                    return <option key={item.strCategory} value={item.strCategory}>{item.strCategory}</option>;
                  })}
                </select>
              </div>
            </div>

            <div className="col">
              <div className="form-group">
                <label htmlFor="sort">Sort by</label>
                <select className="form-control" id="sort" onChange={(event) => this.props.sortById(event.target.value)}>
                      <option value={"Name"}>Name</option>
                      <option value="ID">ID</option>
                </select>
              </div>
            </div>
          </div>
      </form>
    )
  }
}


SearchBar.getInitialProps = async function() {
  const datas = await fetch
  (`https://the-cocktail-db.p.rapidapi.com/filter.php?c=${test}`, {
	"method": "GET",
	"headers": {
		"x-rapidapi-host": "the-cocktail-db.p.rapidapi.com",
		"x-rapidapi-key": "9625f60069msh7df0d5a3ae5a4ddp1d759ejsn0cce4042f495"
	}
  })
  const datasRes = await datas.json();


  return {
    datas: datasRes
  }
}
