class Drinks extends React.Component{

  state = {
    datas: this.props.datas,
    loading: false
  }

  componentDidMount(){
    setTimeout(()=>this.setState({loading: false}),6000)
  }

  render(){
    if (this.state.loading){
      return <p>Loading</p>
    } else{
      return(
        <div className="Drinks">
          <div className="row">
          {this.state.datas.drinks.map(item => {
            return (
              <div className="col-6 col-md-4">
                  <div className="card mb-5">
                    <img className="card-img-top" src={item.strDrinkThumb} alt="..."/>
                    <div className="card-body">
                      <p className="card-text">{item.strDrink}</p>
                      <button className="btn btn-dark btn-sm">More Info</button>
                    </div>
                  </div>
              </div>
            )
          })}
          </div>
        </div>
      )
    }
  }

}

export default Drinks
