import Drink from "./Drinks";

class Drinks extends React.Component{

  state = {
    datas: this.props.datas
  }

  componentWillMount(){
    this.setState({datas: this.props.datas})
  }

  render(){
    if (this.state.datas.drinks.length !== 0){
      return(
        <div className="Drinks">
          <div className="row">
          {this.state.datas.drinks.slice(0,12).map(item => {
            return <Drink key={item.idDrink} {...item}/>
          })}
          </div>
        </div>
      )
    } else{
      return <p>Loading</p>
    }
  }

}

export default Drinks
