import Fetch from "isomorphic-unfetch";
import Layout from "../components/Layout";
import Prices from "../components/Prices";
import Drinks from "../components/Drinks";
import SearchBar from "../components/SearchBar";

export default function Index(props){

  function selectedChange(selectedChange){
    console.log(selectedChange)
  }

  return(
    <Layout>
      <div>
        <h1>Welcome To Cocktails</h1>
        <div className="container">
          <SearchBar selectedChange={selectedChange()} options={props.test}/>
          <Drinks datas={props.datas}/>
        </div>
        <Prices bpi={props.bpi}/>
        {props.bpi.time.updated}
      </div>
    </Layout>
  );
}



let test = "beer"

Index.getInitialProps = async function() {
  const res = await fetch
  ("https://api.coindesk.com/v1/bpi/currentprice.json");
  const data = await res.json();

  const options = await fetch
  ("https://the-cocktail-db.p.rapidapi.com/list.php?c=list", {
  	"method": "GET",
  	"headers": {
  		"x-rapidapi-host": "the-cocktail-db.p.rapidapi.com",
  		"x-rapidapi-key": "9625f60069msh7df0d5a3ae5a4ddp1d759ejsn0cce4042f495"
  	}
  })
  const optionsRes = await options.json();

  const datas = await fetch
  (`https://the-cocktail-db.p.rapidapi.com/filter.php?c=${test}`, {
	"method": "GET",
	"headers": {
		"x-rapidapi-host": "the-cocktail-db.p.rapidapi.com",
		"x-rapidapi-key": "9625f60069msh7df0d5a3ae5a4ddp1d759ejsn0cce4042f495"
	}
  })
  const datasRes = await datas.json();


  return {
    bpi: data,
    test: optionsRes,
    datas: datasRes
  }
}
