import Fetch from "isomorphic-unfetch";
import Layout from "../components/Layout";
import Prices from "../components/Prices";
import Drinks from "../components/Drinks";
import SearchBar from "../components/SearchBar";


const Index = (props) => (
  <Layout>
    <div>
      <h1>Welcome To Cocktails</h1>
      <p>Check current Bitcoin rate</p>
      <div className="container">
        <SearchBar options={props.test}/>
        <Drinks/>
      </div>
      <Prices bpi={props.bpi}/>
      {props.bpi.time.updated}
    </div>
  </Layout>
);

Index.getInitialProps = async function() {
  const res = await fetch
  ("https://api.coindesk.com/v1/bpi/currentprice.json");
  const data = await res.json();

  const options = await fetch
  ("https://the-cocktail-db.p.rapidapi.com/list.php?c=list", {
  	"method": "GET",
  	"headers": {
  		"x-rapidapi-host": "the-cocktail-db.p.rapidapi.com",
  		"x-rapidapi-key": "9625f60069msh7df0d5a3ae5a4ddp1d759ejsn0cce4042f495"
  	}
  })
  const optionsRes = await options.json();

  const datas = await fetch
  ("https://the-cocktail-db.p.rapidapi.com/filter.php?c=Ordinary_Drink", {
	"method": "GET",
	"headers": {
		"x-rapidapi-host": "the-cocktail-db.p.rapidapi.com",
		"x-rapidapi-key": "9625f60069msh7df0d5a3ae5a4ddp1d759ejsn0cce4042f495"
	}
  })

  const datasRes = await datas.json();

  return {
    bpi: data,
    test: data2,
    datas: datasRes
  }
}

export default Index;
