import Fetch from "isomorphic-unfetch";
import Layout from "../components/Layout";
import Link from "next/link";

const Details = (props) => {

  return(
  <Layout>
    <div>
      <img src={props.drink.drinks.strDrinkThumb}/>
      {console.log(props.drink)}
      <Link href="/">Go Back</Link>
    </div>
  </Layout>
  )
}

Details.getInitialProps = async function(context){
  const {id} = context.query
  const res = await fetch
  (`https://the-cocktail-db.p.rapidapi.com/lookup.php?i=${id}`, {
	"method": "GET",
	"headers": {
		"x-rapidapi-host": "the-cocktail-db.p.rapidapi.com",
		"x-rapidapi-key": "9625f60069msh7df0d5a3ae5a4ddp1d759ejsn0cce4042f495"
	}
  })
  const drink = await res.json();
  return {drink: drink};
}

export default Details;
