import Drink from "./Drinks";

class Drinks extends React.Component{

  state = {
    datas: []
  }

  componentDidMount(){
    this.setState({options: this.props.datas})
  }

  render(){
    if (this.state.datas.length !== 0){
      return(
        <div className="Drinks">
          <div className="row">
          {console.log(this.state.datas.drinks.slice(0,12))}
          </div>
        </div>
      )
    } else{
      return <p>Loading</p>
    }
  }

}

export default Drinks
